package ch05;

public class IncreaseDecreaseExam2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int x = 10;
		int y = 10;

		--x; // 9
		System.out.println(x);
		x--; // 8
		System.out.println(x);
		x = x - 1; // 7
		System.out.println(x);

		y++; // 11
		System.out.println(y);
		++y; // 12
		System.out.println(y);
		y = y + 1; // 13
		System.out.println(y);

		/* ÇöÀç x=7, y=13 */

	}

}
