package ch05;

public class IncreaseDecreaseExample3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for (int i = 1; i <= 10; i++) {
			System.out.println(i); // 반복문의 마지막에 있는 실행문 i++는 증가연산
		}

		for (int j = 10; j >= 1; j--) {
			System.out.println(j); // 반복문의 마지막에 있는 실행 문 j--는 감소연산
		}

	}

}
