package ch07;

public class Exam002 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		/* 자바 변수 설명 */
		System.out.println("1. 변수는 하나의 값만 저장할 수 있다.");
		System.out.println("2. 변수는 선언 시에 사용한 타입의 값만 저장할 수 있다.");
		System.out.println("3. 변수는 변수가 선언된 중괄호 ({}) 안에서만 사용 가능하다.");
		System.out.println("4. 변수는 초기값이 저장되자 않은 상태에서 읽을 수 있다. x");

		/* 변수 이름 */
		System.out.println("1.modelName");
		System.out.println("2.class x");
		System.out.println("3.6hour x");
		System.out.println("4.$value");
		System.out.println("5._age x");
		System.out.println("6.int x");

		/* 자바 기본타입 */
		System.out.println("정수타입 :");
		System.out.println("1.byte = 1byte");
		System.out.println("2.char, short = 2byte");
		System.out.println("3.int, float = 4byte");
		System.out.println("4.long, double = 8byte");
		System.out.println("논리타입 :");
		System.out.println("1.boolean = 1byte");

		/* 타입, 변수이름, 리터럴 */
		System.out.println("int age;");
		System.out.println("age = 10;");
		System.out.println("double price = 3.14;");
		System.out.println("타입 : int, double");
		System.out.println("변수이름 : age. price");
		System.out.println("리터럴 : 10, 3.14");

	}

}
