package whileExam;

public class while2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int sum = 0, i = 0;
		while (i < 10) {
			sum += i;
			System.out.println(sum + "," + i);
			i++;
		}
		System.out.println("гу╟Х : " + sum + " , i = " + i);
	}

}
